import React from "react";
import { connect } from "react-redux";
import './anime-search-list.css'
import AnimeSearchListItem from "../anime-search-list-item";
import { makeListInvisible, makeListVisible } from '../../actions'
const AnimeSearchList = ({ animes }) => {
    return (
        <div className="lay" onClick={() => document.querySelector('.lay').style.visibility = "hidden"}>
            <div className="search-list d-flex justify-content-start flex-direction-column">
                <ul class="scroll" style={{ color: "black" }}>
                    {
                        animes.map((anime) => {
                            return (
                                <li key={anime.id} className="search-item border border-dark border-bottom-5">
                                    <AnimeSearchListItem name={anime.name} id={anime.id} image={anime.imageForSearch} />
                                </li>
                            )
                        })
                    }
                </ul>
            </div>
        </div>
    )
}

const mapStateToProps = ({ animeSearchList: { animes } }) => {
    return { animes }

}

const actions = { makeListInvisible, makeListVisible }


export default connect(mapStateToProps)(AnimeSearchList);
