import updateAnimeDetails from "./anime-details";
import updateAnimeList from "./anime-list";
import updateAnimeSearchList from "./anime-search-list";

const initialState = {
    animes: [],
    loading: true,
    error: null,
    animeDetails: {},
    animesSearchList: []
};

const reducer = (state, action) => {
    return {
        animeList: updateAnimeList(state, action),
        animeDetails: updateAnimeDetails(state, action),
        animesSearchList: updateAnimeSearchList(state, action)
    }
}

const reducerOld = (state = initialState, action) => {
    switch (action.type) {
        case 'FETCH_ONGOINGS_REQUEST':
            return {
                ...state,
                animes: [],
                loading: true,
                error: null

            }
        case 'FETCH_ONGOINGS_SUCCESS':
            return {
                ...state,
                animes: action.payload,
                loading: false,
                error: null
            }
        case 'FETCH_ANIME_REQUEST':
            return {
                ...state,
                animeDetails: {},
                loading: true,
                error: null
            }
        case 'FETCH_ANIME_SUCCESS':
            return {
                ...state,
                animeDetails: action.payload,
                loading: false,
                error: null
            }
        case 'FETCH_ANIMES_SEARCH_LIST_REQUEST':
            return {
                ...state,
                loading: false,
                error: null,
                animesSearchList: []
            }

        case 'FETCH_ANIMES_SEARCH_LIST_SUCCESS':
            return {
                ...state,
                loading: false,
                error: null,
                animesSearchList: action.payload
            }
    }
    return state
}

export default reducer;
