const initialState = {
    animes: [],
    loading: true,
    error: null,
    animeDetails: {}
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'FETCH_ONGOINGS_REQUEST':
            return {
                ...state,
                animes: [],
                loading: true,
                error: null

            }
        case 'FETCH_ONGOINGS_SUCCESS':
            return {
                ...state,
                animes: action.payload,
                loading: false,
                error: null
            }
        case 'FETCH_ANIME_REQUEST':
            return {
                ...state,
                loading: true,
                error: null
            }
        case 'FETCH_ANIME_REQUEST':
            return {
                ...state,
                loading: true,
                error: null
            }

    }
    return state
}

export default reducer;
