import React, { Component } from "react";
import './anime-header.css'
import withAnimeService from "../hoc";
import { connect } from "react-redux";
import { animeSearchListRequested, animeSearchListLoaded, animeSearchListClear } from '../../actions'
import { Link } from "react-router-dom";


const AnimeHeader = ({ animeService, animeSearchListRequested, animeSearchListLoaded, animeSearchListClear }) => {
    return (
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark  sticky-top">
            <a className="navbar-brand" href="#">Anime</a>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarTogglerDemo02" aria-controls="navbarTogglerDemo02" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
                <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
                    <li className="nav-item">
                        <Link className="nav-link" to="/animes">Ongoings<span class="sr-only">(current)</span></Link>
                    </li>
                    <li className="nav-item">
                        <Link className="nav-link disabled" href="#">My list</Link>
                    </li>
                    <li className="nav-item">
                        <Link className="nav-link disabled" href="#">Games</Link>
                    </li>
                </ul>
                <form className="search-form form-inline my-2 my-lg-0" style={{ zIndex: '99999' }} action="/animes">
                    <input className="form-control mr-sm-2" type="search" placeholder="Search"
                        onClick={searchClick} onInput={searchAnimes(animeService,
                            animeSearchListRequested, animeSearchListLoaded, animeSearchListClear)} name="search" />
                    <button className="btn btn-outline-light my-2 my-sm-0" type="button" onClick={clickSearch} >Search</button>
                </form>
            </div >
        </nav >
    )
}

const clickSearch = () => {
    const form = document.querySelector('.search-form')
    console.dir(form.children[0].value)

}

const searchAnimes = (animeService, request, loading, clearing) => (event) => {
    document.querySelector('.lay').style.visibility = "visible"
    if (event.target.value.length === 0) {
        clearing()
    }
    else {
        request();
        animeService.searchAnimes(event.target.value).then(res => loading(res))
    }

}

//Make refactoring 

const searchClick = (event) => {
    const form = (event.nativeEvent.path[1])
    const nav = document.querySelector('nav');

    document.querySelector('.lay').style.visibility = "visible"
    document.querySelector('.search-list').style.width = window.getComputedStyle(form).getPropertyValue("width")
    document.querySelector('.search-list').style.top = window.getComputedStyle(nav).getPropertyValue("height")
    document.querySelector('.search-list').style.right = window.getComputedStyle(nav).getPropertyValue("padding-right")

}

let shift = false;
window.addEventListener(`resize`, event => {

    const form = document.querySelector('.search-form')
    const nav = document.querySelector('nav');
    const toggle = document.querySelector('.navbar-toggler');
    if (window.getComputedStyle(toggle).getPropertyValue("display") != 'none' && shift === false) {
        document.querySelector('.lay').style.visibility = "hidden"
        shift = true;
    }

    if (window.getComputedStyle(toggle).getPropertyValue("display") != 'block' && shift === true) {
        document.querySelector('.lay').style.visibility = "hidden"
        shift = false;
    }

    console.log(window.getComputedStyle(toggle).getPropertyValue("display"))
    console.dir(document.querySelector('.navbar-collapse'))
    document.querySelector('.search-list').style.width = window.getComputedStyle(form).getPropertyValue("width")
    document.querySelector('.search-list').style.top = window.getComputedStyle(nav).getPropertyValue("height")
    document.querySelector('.search-list').style.right = window.getComputedStyle(nav).getPropertyValue("padding-right")
}, false);

//Make refactoring 

const actions = { animeSearchListRequested, animeSearchListLoaded, animeSearchListClear }

export default withAnimeService()(connect(null, actions)(AnimeHeader));
