import React from "react";
import './anime-list-item.css';
import { Link } from 'react-router-dom';


const AnimeListItem = ({ id, title, image }) => {
    console.log(image);
    const url = `/animes/${id}`
    return (

        <div className="anime-item mx-3">
            <Link to={url} className="anime-field" onMouseOver={changeItemSize} onMouseOut={changeItemSize}>
                <img src={image} alt="cover" className="anime-image" />
                <footer className="text-light">
                    {title}
                </footer>
            </Link >
        </div>

    )
}

const changeItemSize = (event) => {
    console.dir(event)
    const img = event.nativeEvent.path[1].children[0];
    const text = event.nativeEvent.path[1].children[1];
    if (event.type == "mouseover") {
        img.style.height = '80%'
        img.style.width = '100%';
        text.style.marginTop = '1em'
    }

    else {
        const wrapper = event.nativeEvent.path[1];
        console.log(wrapper)
        wrapper.style.height = '418px'
        wrapper.style.width = '225px'
        img.style.height = '75%'
        img.style.width = '100%'
        //img.style.transform = 'scale(1)'
        text.style.marginTop = '0.5em'
        //img.style.height = '75%'
        //text.style.width = '90%';
        //text.style.marginTop = '0.5em';
    }
}

export default AnimeListItem;
