import React, { Component } from "react";
import './anime-details.css'
import { connect } from "react-redux";
import withAnimeService from "../hoc";
import * as actions from '../../actions'
import { withRouter } from "react-router";

const AnimeDetails = ({ anime }) => {
    console.log(anime)

    return (
        <div className="container-fluid mt-5 text-light" >
            <div className="row">
                <div className="col-2">
                    <img src={anime.image} className="img-fluid rounded-start p-3" />
                </div>
                <div className="col">
                    <h1>{anime.name}</h1>
                    <ul className="p-0">
                        <li>Рейтинг: <span className="text-secondary">{anime.score}</span></li>
                        <li>Количество эпизодов:  <span className="text-secondary">{anime.episodes_aired}</span></li>
                        <li>Жанры: {anime.genres.map((genre, index) => {
                            console.log(index)
                            console.log(anime.genres.length)
                            if (index !== anime.genres.length - 1) {
                                return (
                                    <span>{genre.russian} ,</span>
                                )
                            }
                            else {
                                return (
                                    <span>{genre.russian}</span>
                                )
                            }
                        }
                        )}</li>
                        <li>Описание: <span className="text-secondary">{anime.description}</span></li>
                    </ul>
                </div>

            </div>
        </div >


    )
}

class AnimeDetailsContainer extends Component {
    componentDidMount() {
        console.log("detail mount")
        this.props.animeRequested()
        this.props.animeService.getAnime(this.props.id).then(res => this.props.animeLoaded(res))
    }

    componentDidUpdate(prevProps) {
        if (prevProps.location != this.props.location) {
            console.log("detail update")

            this.props.animeRequested()
            this.props.animeService.getAnime(this.props.id).then(res => this.props.animeLoaded(res))
        }
    }

    render() {
        if (Object.keys(this.props.anime).length) {
            return <AnimeDetails anime={this.props.anime} />
        }
        else {
            return "nnnn"
        }
    }
}

const mapStatetoProps = ({ animeDetails: { anime, loading, error } }) => {
    return { anime, loading, error };
};

export default withRouter(withAnimeService()(connect(mapStatetoProps, actions)(AnimeDetailsContainer)))
