const initialState = {
    loading: true,
    error: null
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'FETCH_ONGOINGS_REQUEST':
            console.log("HI");
            return {
                animes: [],
                loading: true,
                error: null
            }
        case 'FETCH_ONGOINGS_SUCCESS':
            console.log("Finish")
            return {
                animes: action.payload,
                loading: false,
                error: null
            }
    }
    return state
}

export default reducer;
