const updateAnimeSearchList = (state, action) => {

    if (state === undefined) {
        return {
            visibility: false,
            search: '',
            animes: [],
            loading: true,
            error: null
        };
    }
    switch (action.type) {
        case 'FETCH_ANIME_SEARCH_LIST_REQUEST':
            return {
                ...state,
                animes: [],
                loading: true,
                error: null,
            }

        case 'FETCH_ANIME_SEARCH_LIST_SUCCESS':
            return {
                ...state,
                animes: action.payload,
                loading: false,
                error: null,
            }
        case 'FETCH_ANIME_SEARCH_LIST_FAILURE':
            return {
                ...state,
                animes: [],
                loading: false,
                error: action.payload
            };
        case 'CLEAR_ANIME_SEARCH_LIST':
            return {
                ...state,
                animes: [],
                loading: false,
                error: null,
            }
        case 'TYPE_ANIME_SEARCH':
            return {
                ...state,
                search: action.payload
            }
        case 'MAKE_LIST_VISIBLE':
            return {
                ...state,
                visibility: true
            }
        case 'MAKE_LIST_INVISIBLE':
            return {
                ...state,
                visibility: false
            }
        default:
            return state.animeSearchList;
    }
}

export default updateAnimeSearchList;
