import React, { Fragment, useState } from "react";
import { Link } from "react-router-dom";
import "./my-list-item.css"
import { addToMyList, removeFromMyList } from "../../actions"



const MyListItem = ({ anime }) => {
    const { grade, setGrade } = useState(anime.grade);
    return (
        <Fragment>
            <td className="name">
                <Link to={`/animes/${anime.id}`}>{anime.name}</Link>
            </td>
            <td className="episodes">
                <input className="my-list-item-stats" type="text" onBlur={() => console.log("bfbffb")} />
            </td>
            <td className="grade">
                <input className="my-list-item-stats" onBlur={changeGrade(anime)} type="text"
                    onChange={handeGradeChange(setGrade)} value={grade} />
            </td>
        </Fragment>
    )
}

const handeGradeChange = (setGrade) => (event) => {
    console.log(setGrade)
    setGrade(event.target.value)
}
const changeGrade = (anime) => (event) => {
    removeFromMyList(anime)
    const newItem = { ...anime, grade: event.target.value }
    addToMyList(newItem);
}

export default MyListItem;
