import React from "react";
import './anime-header.css'
import withAnimeService from "../hoc";
import AnimeService from "../../services/anime-service";

const AnimeHeader = (animeService) => {
    return (
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark  sticky-top">
            <a className="navbar-brand" href="#">Anime</a>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarTogglerDemo02" aria-controls="navbarTogglerDemo02" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>

            <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
                <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
                    <li className="nav-item">
                        <a className="nav-link" href="#">Ongoings<span class="sr-only">(current)</span></a>
                    </li>
                    <li className="nav-item">
                        <a className="nav-link" href="#">My list</a>
                    </li>
                    <li className="nav-item">
                        <a className="nav-link" href="#">Games</a>
                    </li>
                </ul>
                <form className="form-inline my-2 my-lg-0" style={{ zIndex: '99999' }}>
                    <input className="form-control mr-sm-2" type="search" placeholder="Search" onClick={searchClick} onInput={searchAnime(animeService)} />
                    <button className="btn btn-outline-light my-2 my-sm-0" type="submit" >Search</button>
                </form>
            </div >
        </nav >
    )
}

const searchAnime = (animeService) => (event) => {
    console.log(animeService.searchAnime(event.target.value))
    //console.dir(event.target.value);

}
const searchClick = (event) => {
    const form = (event.nativeEvent.path[1])
    const nav = document.querySelector('nav');
    console.log(window.getComputedStyle(form).getPropertyValue("height"))
    console.log(window.getComputedStyle(form).getPropertyValue("width"))
    console.log(window.getComputedStyle(nav).getPropertyValue("padding-right"))
    console.log(window.getComputedStyle(nav).getPropertyValue("padding-top"))
    console.log(window.getComputedStyle(nav).getPropertyValue("height"))

    let a = window.getComputedStyle(nav).getPropertyValue("padding-top")
    let b = window.getComputedStyle(nav).getPropertyValue("height")
    let c = parseInt(a, 10) * 2 + parseInt(b, 10)

    document.querySelector('.lay').style.visibility = "visible"
    document.querySelector('.searchList').style.width = window.getComputedStyle(form).getPropertyValue("width")
    document.querySelector('.searchList').style.top = window.getComputedStyle(nav).getPropertyValue("height")
    document.querySelector('.searchList').style.right = window.getComputedStyle(nav).getPropertyValue("padding-right")

}

export default withAnimeService()(AnimeHeader);
