import React, { Component } from "react";
import './anime-list.css';
import AnimeListItem from "../anime-list-item";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import withAnimeService from "../hoc";
import { ongoingsRequested, ongoingsLoaded } from "../../actions";
import * as actions from '../../actions'




const AnimeList = ({ animes }) => {
    return (
        <ul className="book-list">
            {
                animes.map((anime) => {
                    return (
                        <li key={anime.id}>
                            <AnimeListItem title={anime.name} image={anime.image} />
                        </li>
                    )
                })
            }

        </ul>
    )
}

class AnimeListContainer extends Component {
    componentDidMount() {
        this.props.ongoingsRequested()
        console.log(this.props)
        this.props.animeService.getOngoings(3).then(res => ongoingsLoaded(res))

    }

    render() {
        return <AnimeList animes={this.props.animes} />
    }

}

const mapStatetoProps = ({ animes, loading, error }) => {
    return { animes, loading, error };
};


export default withAnimeService()(connect(mapStatetoProps, actions)(AnimeListContainer))
