import React from "react";
import { connect } from "react-redux";
import './anime-search-list.css'

const AnimeSearchList = ({ animes }) => {
    return (
        <div className="lay" onClick={() => document.querySelector('.lay').style.visibility = "hidden"}>
            <div className="searchList d-flex justify-content-start flex-direction-column">
                <ul class="scroll" style={{ color: "black" }}>
                    {
                        animes.map((anime) => {
                            return (
                                <li key={anime.id} className="search-item border border-dark border-bottom-5">
                                    {anime.name}
                                </li>
                            )
                        })
                    }
                </ul>
            </div>
        </div>
    )
}

window.addEventListener(`resize`, event => {
    console.log("hh")
}, false);

const mapStateToProps = ({ animeSearchList: { animes } }) => {
    return { animes }

}


export default connect(mapStateToProps)(AnimeSearchList);
