import React, { Fragment, useState } from "react";
import { Link } from "react-router-dom";
import "./my-list-item.css"
import { addToMyList, removeFromMyList } from "../../actions"
import { connect } from "react-redux";



const MyListItem = ({ anime }) => {
    const [grade, setGrade] = useState(anime.grade);
    console.log("Сеттер")
    console.log(setGrade)
    return (
        <Fragment>
            <td className="name">
                <Link to={`/animes/${anime.id}`}>{anime.name}</Link>
            </td>
            <td className="episodes">
                <input className="my-list-item-stats" type="text" onBlur={() => console.log("bfbffb")} />
            </td>
            <td className="grade">
                <input className="my-list-item-stats" onBlur={changeGrade(anime, grade)} type="text"
                    onChange={handeGradeChange(setGrade)} value={grade} />
            </td>
        </Fragment>
    )
}

const handeGradeChange = (setGrade) => (event) => {
    setGrade(event.target.value)
}
const changeGrade = (anime, grade) => (event) => {
    console.log("лялял")
    console.log(grade)
    removeFromMyList(anime)
    const newItem = { ...anime, grade }
    addToMyList(newItem);
}

actions = { addToMyList, removeFromMyList }
export default connect(null, actions)(MyListItem);
