const initialState = {
    animes: [],
    loading: true,
    error: null,
    animeDetails: {},
    animeSearchList: []
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'FETCH_ONGOINGS_REQUEST':
            return {
                ...state,
                animes: [],
                loading: true,
                error: null

            }
        case 'FETCH_ONGOINGS_SUCCESS':
            return {
                ...state,
                animes: action.payload,
                loading: false,
                error: null
            }
        case 'FETCH_ANIME_REQUEST':
            return {
                ...state,
                animeDetails: {},
                loading: true,
                error: null
            }
        case 'FETCH_ANIME_SUCCESS':
            return {
                ...state,
                animeDetails: action.payload,
                loading: false,
                error: null
            }
        case 'FETCH_ANIME_SEARCH_LIST_REQUEST':
            return {
                ...state,
                loading: false,
                error: null,
                animeSearchList: []
            }

        case 'FETCH_ANIME_SEARCH_LIST_SUCCESS':
            return {
                ...state,
                loading: false,
                error: null,
                animeSearchList: action.payload
            }
    }
    return state
}

export default reducer;
