import React from 'react'
import AnimeHeader from '../anime-header'
import AnimeList from '../anime-list'
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import AnimeDetails from '../anime-details';
import './app.css'

const App = () => {
    return (
        <main className="bg-dark" >
            <AnimeHeader />
            <div className="lay" onClick={() => document.querySelector('.lay').style.visibility = "hidden"}>
                <div className="searchList d-flex justify-content-start flex-direction-column">
                    <div class="prokrutka" style={{ height: "10px" }}>
                        А тут много-много разного текста и прочей информации. А тут много-много разного текста и прочей информации. А тут много-много разного текста и прочей информации. А тут много-много разного текста и прочей информации. А тут много-много разного текста и прочей информации. А тут много-много разного текста и прочей информации. А тут много-много разного текста и прочей информации. А тут много-много разного текста и прочей информации.
                    </div>
                </div>
            </div>
            <Switch>
                <Route path="/animes" component={AnimeList} exact />
                <Route path="/animes/:id" render={({ match }) => {
                    const { id } = match.params;
                    return <AnimeDetails id={id} />
                }} exact />
            </Switch>
        </main >
    );
};

export default App;
