import React, { Fragment } from "react";
import MyListItem from "../my-list-item";
import { connect } from "react-redux";

const Table = ({ animes }) => {
    console.log(animes)
    return (
        <table class="table table-dark">
            <thead>
                <tr>
                    <th scope="col">#</th>
                    <th scope="col">Название</th>
                    <th scope="col">Количество Эпизодов</th>
                    <th scope="col">Оценка</th>
                </tr>
            </thead>
            <tbody>
                {animes.map((anime, index) => {
                    return (
                        <Fragment>
                            <th scope="row">index</th>
                            <MyListItem anime={anime} />
                        </Fragment>
                    )
                })}
            </tbody>
        </table>
    )
}

const MyList = ({ animes }) => {
    console.log(animes)
    const scheduled = animes.filter(anime => anime.category === "Scheduled");
    return (
        <Table animes={scheduled} />
    )
}

const mapStatetoProps = ({ myList: { animes } }) => {
    return { animes };
};

export default connect(mapStatetoProps)(MyList);
